export default (cards = [], action) => {
  switch (action.type) {
    case "UPDATE":
      return cards.map((post) => post._id === action.payl)
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return [...cards, action.payload];
    default:
      return cards;
  }
};
