export default (cards = [], action) => {
  switch (action.type) {
    case "UPDATE":
      return cards.map((card) =>
        card._id === actions.payload._id ? actions.payload : card
      );
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return [...cards, action.payload];
    default:
      return cards;
  }
};
