import React from "react";
import {
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography,
} from "@material-ui/core";
import ThumbUpAltIcon from "@material-ui/icons/ThumbUpAlt";
import DeleteIcon from "@material-ui/icons/Delete";
import MoreHorizonIcon from "@material-ui/icons/MoreHoriz";

import useStyles from "./styles";

const NBACard = ({ card }) => {
  const classes = useStyles();

  return (
    <Card className={classes.card}>
      <CardMedia
        className={classes.media}
        image={card.selectedFile}
        title={card.title}
      />

      <div className={classes.overlay}>
        <Typeography variant="h6">{card.creator}</Typeography>
        <Typeography variant="body2">
          {moment(card.createdAt).fromNow()}
        </Typeography>
      </div>

      <div className={classes.overlay2}>
        <Button style={{ color: "white" }} size="small" onClick={() => {}}>
          <MoreHorizonIcon fontSize="default" />
        </Button>
      </div>

      <div className={classes.details}>
        <Typeography variant="body2" color="textSecondary">
          {card.tags.map((tag) => `#${tag} `)}
        </Typeography>
      </div>

      <CardContent>
        <Typeography className={classes.title} variant="h5" gutterBottom>
          {card.message}
        </Typeography>
      </CardContent>

      <CardActions className={classes.cardActions}>
        <Button size="small" color="primary" onClick={() => {}}>
          <ThumbUpAltIcon fontSize="small" />
          Like
          {card.likeCount}
        </Button>
        <Button size="small" color="primary" onClick={() => {}}>
          <DeleteIcon fontSize="small" />
          Delete
        </Button>
      </CardActions>
    </Card>
  );
};

export default Card;
